package Strings;

public class stringReplacingCharacters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s = "Welcome to JAVA";
		//System.out.println(s.replace('o', 'a'));//replaces all letter o with a 
		System.out.println(s.replace('A', 'o'));
		
		System.out.println(s.replace("JAVA", "Donald"));// .replace( old char , new char)
		System.out.println(s.replace("JA", "HO")); 
	}

}
